import React, { Component } from "react";

class Api extends Component {
    state = {
        data: [],
    }

    componentDidMount() {
        try {
            const url = 'https://economia.awesomeapi.com.br/json/daily/%20/:numero_dias'

            fetch(url) 
                .then((result) => result.json())
                .then((result) => {
                    this.setState({
                        data: result,
                    })
                })
        } catch (error) {
            console.error
        }
    }

    render() {
        const {data} = this.state

        const result = data.map((entry, index) => {
            return <li key={index}>{entry}</li>
        })

        return <ul>{result}</ul>
    }
}

export default Api
